import {React,useState,useEffect} from 'react'
import axios from 'axios'

const apiurl=` https://jsonplaceholder.typicode.com/posts`
import pager from './pager'

export default (props) =>{


 {

    var [posts, setStatePosts] =useState([]);
    var [DefaultMaxDisplayposts] =useState(8);
    var [DefaultButton,SetStateDefaultButton] =useState(1);

    var lastindex=[DefaultButton*SetStateDefaultButton]
   
    useEffect(()=>
    {
    axios.get(apiurl).then((result)=>this.setStatePosts(result.data))
    },[])

   var totalposts = posts.length
    return (<>
             

       <div className='container p-5'>
       <h1 className='text-center pb-3'>REACT Hooks pagination</h1>
     
  <pager TotalpostsinAPI={totalposts}
   maxDisplayrecordsperButton={DefaultMaxDisplayposts}/>
         <div className='row'>
         {

posts.map((res,i)=>
{
    return(<ul  key={i}>
        <li>
            <h4>{res.id}.{res.title}</h4>
            <p>res.body</p>
        </li>
        
        </ul>)
})

 }


         </div>

       </div>
       
      

      </>)
 }
}
