import {React,useState,useEffect} from 'react'
import axios from 'axios'

const apiurl=`https://fakestoreapi.com/products`

export default (props) =>{


 {

    var [products,setStateProducts] =useState([]);
   
    useEffect(()=>
    {
    axios.get(apiurl).then((result)=>this.setStateProducts(result.data))
    },[])


    return (<>
             

       <div className='container p-5'>
       <h1 className='text-center'>REACT Hooks useeffect</h1>
      
         <div className='row'>
         {

products.map((res,i)=>
{
    return(<div className='col-lg-3' key={i}>
        
        <div className="card">
  <img className="card-img-top w-50 mx-auto my-2" src={res.image}  />
  <div className="card-body text-center">
    <h5 className="card-title">{res.title}</h5>
    <h5 className="card-title">{res.price}</h5>
    <p className="card-text"> {res.description.slice(0,100)}</p>
    <a href="#" className="btn btn-primary">Go somewhere</a>
  </div>
</div>
        </div>)
})

 }


         </div>

       </div>
       
      

      </>)
 }
}
