import {React,Component} from 'react';
import {connect} from 'react-redux'
import empAction from  './../../Actions/empActions'
import { bindActionCreators } from 'redux';
class ReactReduxConnection extends Component{
    constructor(props){
        super(props);
        this.state={}
    }
    componentDidMount()
    {
        this.props.empAction();

    }
    render(){
        return(<>
         <div className='container p-5'>

         <h2 className='text-center'> ReactReduxConnection Example</h2>
         </div>
        {
          this.props.empdata.map((res,i)  =>
          {
              return(<ul key={i}>
               <li>{res.id}</li>
               <li>{res.name}</li>
               <li>{res.email}</li>

              </ul>)
          })

        }


        </>)
    }
}
const mapStateToProps =(state) =>{
    console.log(state)
return ({
    empdata:state.EmpReducer
})
}
 const mapDispatchToProps =(dispatch) =>{
    return  bindActionCreators({empAction},dispatch)
}
//export default ReactReduxConnection
export default connect(mapStateToProps,mapDispatchToProps)(ReactReduxConnection)
