import React,{ useState,useEffect} from "react";
import axios from 'axios';
import { Link } from "react-router-dom";

const url="https://jsonplaceholder.typicode.com/users"
 export default() =>{
    let [userData,setStateuseData]= useState([]);
function getData()
{

    axios.get(url).then((result) =>  axios.get(url).then((result) => setStateuseData(result.data))(result.data))

    

       useEffect(()=>{getData()},[]);
        }
    
    let DeleteHandler=(id)=>
    {
      
        
       if( window.confirm(`Delete the Record Number ${id} ?`)){
        axios.delete(`${url}/${id}`)
         getData()
       }
       
    }
    
    
        return(<> 
        
            <div className="p-5 text-center">
           <h2 className="text-center">FETCH RESTAPI USING GLOBAL URL/CLIENT</h2>
           <table className="table table-dark table-bordered">
             <thead>
                 <tr>
                     <th>id</th>
                     <th>name</th>
                     <th>email</th>
                     <th>website</th>
                     <th colSpan="4" className="text-center">Action</th>
                 </tr>
             </thead>
             <tbody>

             
   {
         
        userData.map((res,i)=>{
             return(<tr key={i}>
                  <td>{res.id}</td>
                  <td>{res.name}</td>
                  <td>{res.email}</td>
                  <td>{res.website}</td>
                  
                  <td><Link to={`/restapi/view/${res.id}`} className="btn btn-primary">view</Link></td>

                  <td><Link to={`/restapi/view/${res.id}`}className="btn btn-danger">edit</Link></td>
                  <td><button  onClick={DeleteHandler(res.id)} className="btn btn-danger">Delete</button></td>
                  <td><button className="btn btn-secondary">view</button></td>
          </tr>   )
         })

          }
          
          </tbody>
            </table>
            </div>
            </>  )
    
 }
