import React,{ Component } from "react";
import axios from 'axios';
import { Link } from "react-router-dom";

const url="https://jsonplaceholder.typicode.com/users"
  class Restapi extends Component
{
    constructor(props) 
    {

        super(props)
        this.state ={
             userData:[]
        }
    }
    
    /*DeleteHandler=(id)=>
    {
      
        
       if( window.alert(`Selected Recors number is ${id} ?`)){
        axios.delete(`${url}/${id}`)
        axios.get(url).then((result)=> this.setState({userData:result.data})) 
       }
       
    }*/
    
    render(){
         console.log(this.props)
        return(<> 
        
            <div className="p-5 text-center">
           <h2 className="text-center">FETCH RESTAPI USING GLOBAL URL/CLIENT</h2>
           <table className="table table-dark table-bordered">
             <thead>
                 <tr>
                     <th>id</th>
                     <th>email</th>
                     <th>email</th>
                     <th>name</th>
                     <th colSpan="3" className="text-center">Action</th>
                 </tr>
             </thead>
             <tbody>

             </tbody>
   {
         
         this.state.userData.map((res,i)=>{
             return(<tr key={i}>
                  <td>{res.id}</td>
                  <td>{res.name}</td>
                  <td>{res.email}</td>
                  <td>{res.website}</td>
                  
                  <td><Link to={`/restapi/view/${res}`} className="btn btn-primary">view</Link></td>

                  <td><Link to={`/restapi/view/${res}`}className="btn btn-danger">edit</Link></td>
                  <td><button  onClick={()=>this.DeleteHandler(res.id)} className="btn btn-danger">Delete</button></td>
                  <td><button className="btn btn-secondary">view</button></td>
          </tr>   )
         })

          }
          
        
            </table>
            </div>
            </>  )
    }
    componentDidMount(){  axios.get(url).then((result)=> this.setState({userData:result.data}));}
}
export default Restapi
