import React,{Component} from 'react'
import axios from 'axios'
//import {Link} from 'react-router-dom'
 
const url =`http://localhost:3001/users`
export default class Signup extends Component{
     constructor(props){
         super(props)
         this.state={
             uname:'',
             email:'',
             phone:'',
             textConformation:''
         }
     }
    unameHandling =(e) =>
    {
      this.setState({uname:e.target.value})

    }
    emailHandling =(e) =>
    {
      this.setState({email:e.target.value})

    }
    phoneHandling =(e) =>
    {
      this.setState({phone:e.target.value})

    }
     
   submitHandler = (e) => {
        {
            e.preventDefault();

           var formdata ={
            'uname':this.state.uname,
            'email':this.state.email,
            'phone':this.state.phone
           }
        }
        //alert(JSON.stringify(FormData));
        axios.post(url,formdata).then(()=>{
            window.alert("....CREATED,..")
            this.props.history.push('/Myapi.json'); })
        
        
       // this.props.history.push('/restapi'))
       // window.alert("....CREATED,.."));
        // .then(()=>this.setState({txtConformation:'Account created...'}))
           // .catch(()=>this.setState({txtConformation:'Account failed...'}))
    }
    render(){
           var {uname,email,phone,textConformation}=this.state
        return(<>
        
        <div className='container p-3'>
            <h4 className='py-3  text-center'>User Registration</h4>
        <div className='container w-50'>
            <form onSubmit={this.submitHandler.bind(this)}>
            <div class="mb-3">
            <label className='form-label'>Name</label>
            <input type="text" class="form-control" value={uname} onChange={()=> this.unameHandling.bind(this)} />
        </div>
        <div class="mb-3">
            <label className='form-label'>Email</label>
            <input type="email" class="form-control" value={email} onChange={()=> this.emailHandling.bind(this)} />
        </div>
        <div class="mb-3">
            <label className='form-label'>Phone</label>
            <input type="number" class="form-control" value={phone} onChange={()=> this.phoneHandling.bind(this)} />
        </div>
        <div class="mb-3">
           
            <button type="submit" class="btn-btn-primary">create acount</button>
        </div>
        <p class="text-primary">{textConformation}</p>
            </form>
        </div>
         

</div>
        
        
            
</> )
    }
    
}
