  <pager TotalpostsinAPI={totalposts}
   maxDisplayrecordsperButton={DefaultMaxDisplayposts}
   getButtonNumber={(bno)=>handleButonNumberChanges(bno)}
   />
         <div className='row'>
         {

DisplayDefaultButtonPosts.map((res,i)=>
{
    return(<ul  key={i}>
        <li>
            <h4>{res.id}.{res.title}</h4>
            <p>res.body</p>
        </li>
        
        </ul>)
})

 }


         </div>

       </div>
       
      

      </>)
 }
}
